	if(max(age) > max(profile$breaks)) {
	   warning("aligning last break point in profile$breaks ")
	   profile$breaks[length(profile$breaks)] <- max(age)
	}
        Hi <- numeric(2)
        surv <- numeric(length(age))
        ic <- 1

        for (ti in age) {
			t2 <- c(profile$breaks[profile$breaks < ti], ti)
			k <- length(t2)
			t1 <- c(0,t2[1:(k-1)])
			t21 <- t2-t1

			Hi[1] <- 0
