h2a <-function (p, t1, t2) {
            a0 <- p[1]
            At <- p[2]
            Bt <- p[3]
            Rt <- p[4]
            t21 <- (t2 - t1)
            if (t21 >= 0) {
                    f1 <- Bt * exp(-At * (t21))
                    f2 <- At * exp(-Bt * (t21))
                    f <- (f1 - f2)
                    dfdu <- At * f1 - Bt * f2
                    df2dudt <- Bt * Bt * f2 - At * At * f1
                    return(-Rt * (dfdu * dfdu + df2dudt * f)/f/f)
            }
            else return(0)
}

s2a <- function (p, t1, t2) {
        a0 <- p[1]
        At <- p[2]
        Bt <- p[3]
        Rt <- p[4]
        t21 <- (t2 - t1)
	if(t21 > 0) {
        f1 <- Bt * exp(-At * (t21))
        f2 <- At * exp(-Bt * (t21))
        f <- (f1 - f2)
	dfdu <- At*f1-Bt*f2
        return(Rt*dfdu/f)
	} else return(0)
}



