              #1           0           0          1
              #2           0           0          1
              #3           0           0          1
              #4           0           0          1
              #5           0           0          1

              xx2=xx
              #> xx[1:10,]
              #      cig_former cig_current
              # [1,]          0           1
              # [2,]          0           1
              # [3,]          0           1
              # [4,]          0           1
              # [5,]          0           1
              # [6,]          0           1
              # [7,]          0           1
              # [8,]          0           1
              # [9,]          0           1
              #[10,]          0           1

              ############# [1] Create reference dummy variable first in case it doesn't have one #######

              if(sum(rowSums(xx)==0,na.rm=T) >0)  { # # then referenece variable is not included in this matrix --> make it then

                    yy = rep(0,nrow(xx))
                    yy[rowSums(xx)==0] = 1
                    xx2=cbind(strata1=yy,xx)
                    xx2[1:5,]
                    #  strata1 STUDY_CPSII STUDY_EAGLE STUDY_PLCO
                    #1  0           0           0          1
                    #2  0           0           0          1
                    #3  0           0           0          1
                    #4  0           0           0          1
                    #5  0           0           0          1


                    sum(rowSums(xx2)>1)
                    #[1] 0
                    sum(rowSums(xx2)==0)
                    #[1] 0

              }# end of


              ############ a single factor variable ############################

              stVar=rep(NA,nrow(xx2))

              for(u in 1:ncol(xx2)){

                    indic=(xx2[,u]==1)
                    stVar[indic] = u

              }# end of u
              table(stVar)
              #stVar
              #   1    2    3    4
              #3003 1371 3899 3197

              ans=list(stVar=as.factor(stVar), designMat=xx2)
              ans



}# end of myStrataVar


