											#indic.all

									 }#end of

					  p0=p1
					  b.min = 0	  # minimum slope
					  sps = seq(b.min, b.h, length.out=k);sps



						  for(i in 1:length(sps)){

								tt=myCheck.small(sps[i], x, y, p0)
								ind = tt$indic.all
								if(i==1) ans=ind
								if(i>1) ans=c(ans,ind)

						  }#for(i in 1:length(sps)){
						# > ans
